			// 	return datum });

			if (this.allLabels.length==1) // a single label
				return;
			//console.log(util.inspect(dataset,{depth:1}));
			if (this.debug) console.log("trainBatch start");
			var learnFile = svmcommon.writeDatasetToFile(
					dataset, this.bias, /*binarize=*/false, this.model_file_prefix+"_"+this.timestamp, "SvmLinear", FIRST_FEATURE_NUMBER);
			var modelFile = learnFile.replace(/[.]learn/,".model");

			var command = this.train_command+" "+this.learn_args+" "+learnFile + " "+modelFile;
			console.log("running "+command);

			var result = child_process.execSync(command);
			if (result.code>0) {
				console.dir(result);
				console.log(fs.readFileSync(learnFile, 'utf-8'));
				throw new Error("Failed to execute: "+command);
			}

			this.modelFileString = modelFile;

			if (this.debug) console.log("trainBatch end");
		},

		setModel: function(modelFileString) {
			// this.modelFileString = modelFileString;
			this.modelString = fs.readFileSync(modelFileString, "utf-8")
			this.mapLabelToMapFeatureToWeight = modelStringToModelMap(this.modelString);
			this.allLabels = Object.keys(this.mapLabelToMapFeatureToWeight);
			if (this.debug) console.dir(this.mapLabelToMapFeatureToWeight);
		},

		getModelWeights: function() {
			if (!this.mapLabelToMapFeatureToWeight)
                                this.setModel(this.modelFileString)
			return (this.multiclass? this.mapLabelToMapFeatureToWeight: this.mapLabelToMapFeatureToWeight[1]);
		},

		/**
		 * @param features - a feature-value hash.
		 * @param explain - int - if positive, an "explanation" field, with the given length, will be added to the result.
		 * @param continuous_output if true, return the net classification score. If false [default], return 0 or 1.
		 * @return the binary classification - 0 or 1.
		 */

		classifyBatch: function(trainset) {

			// console.log(JSON.stringify(this.modelFileString, null, 4))
			_.each(trainset, function(value, key, list){
				trainset[key].output = 0
			}, this)

			var testFile = svmcommon.writeDatasetToFile(
                                        trainset, this.bias, /*binarize=*/false, "/tmp/test_"+this.timestamp, "SvmLinear", FIRST_FEATURE_NUMBER);

			var command = this.test_command+" "+testFile + " " + this.modelFileString + " /tmp/out_" + this.timestamp;

			var output = child_process.execSync(command)
			console.log(command)

			var result = fs.readFileSync("/tmp/out_" + this.timestamp, "utf-8").split("\n")

			return result
		},

		classify: function(features, explain, continuous_output) {

			if (!this.mapLabelToMapFeatureToWeight)
				this.setModel(this.modelFileString)

			if (this.allLabels.length==1) {  // a single label
				var result = (
						!continuous_output?   this.allLabels[0]:
							!this.multiclass? 1.0:
							                  [[this.allLabels[0], 1.0]]);
				return (explain>0?
						{
							classes: result,
							explanation: ["Single label ("+result+") - no classification needed"],
						}:
						result);
			}
			var labels = [];
			if (explain>0) var explanations = [];
			for (var label in this.mapLabelToMapFeatureToWeight) {
				var mapFeatureToWeight = this.mapLabelToMapFeatureToWeight[label];

				var scoreWithExplain = svmcommon.classifyWithModelMap(
					mapFeatureToWeight, this.bias, features, explain, /*continuous_output=*/true, this.featureLookupTable);

				var score = (explain>0? scoreWithExplain.classification: scoreWithExplain);

				var labelAndScore = [parseInt(label), score];
				if (scoreWithExplain.explanation && explain>0) {
					labelAndScore.push(this.multiclass?
						scoreWithExplain.explanation.join(" "):
						scoreWithExplain.explanation)
				}

				labels.push(labelAndScore);
			}

			labels.sort(function(a,b) {return b[1]-a[1]}); // sort by decreasing score

			if (explain>0) {
				if (this.multiclass) {
					var explanations = [];
					labels.forEach(function(datum) {
						explanations.push(datum[0]+": score="+JSON.stringify(datum[1])+" features="+datum[2]);
						datum.pop();
					});
				} else {
					var explanations = (labels[0][0]>0? labels[0][2]: labels[1][2])
				}
			}

			var result = (
				!continuous_output?   labels[0][0]:
					!this.multiclass? (labels[0][0]>0? labels[0][1]: labels[1][1]):
					                  labels);
			return (explain>0?
				{
					classes: result,
					classification: result,
					explanation: explanations,
				}:
				result);
		},

		/**
		 * Link to a FeatureLookupTable from a higher level in the hierarchy (typically from an EnhancedClassifier), used ONLY for generating meaningful explanations.
		 */
		setFeatureLookupTable: function(featureLookupTable) {
			this.featureLookupTable = featureLookupTable;
		},

		toJSON: function() {
			return this.mapFeatureToWeight;
		},

		fromJSON: function(json) {
			this.mapFeatureToWeight = json;
		},
};


/*
 * UTILS
 */

var NEWLINE = require('os').EOL;

var LIB_LINEAR_MODEL_PATTERN = new RegExp(
		"[\\S\\s]*"+    // skip the beginning of string
		"^label (.*)"+NEWLINE+  // parse the label-list line
		"^nr_feature .*"+NEWLINE+  // parse the feature-count line (not used)
		"^bias (.*)"+NEWLINE+  // parse the bias line (not used - we use our own bias)
		"^w"+NEWLINE+                // start of weight matrix
		"([\\S\\s]*)" + // parse the weights
		"", "m");

var MIN_WEIGHT = 1e-5; // weights smaller than this are ignored, to save space

/**
 * A utility that converts a model in the SvmLinear format to a matrix of feature weights per label.
 * @param modelString a string.
 * @returns mapLabelToMapFeatureToWeight.
 */
function modelStringToModelMap(modelString) {
	var matches = LIB_LINEAR_MODEL_PATTERN.exec(modelString);
	if (!matches) {
		console.log(modelString);
		throw new Error("Model does not match SVM-Linear format");
	};
	var labels = matches[1].split(/\s+/);
	var mapLabelToMapFeatureToWeight = {};
	for (var iLabel in labels) {
		var label = labels[iLabel];
		mapLabelToMapFeatureToWeight[label]={};
	}

	var weightsMatrix = matches[3];
	// each line represents a feature; each column represents a label:

	var weightsLines = weightsMatrix.split(NEWLINE);
	for (var feature in weightsLines) {
		var weights = weightsLines[feature].split(/\s+/);
		weights.pop(); // ignore lal]st weight, which is empty (-space)
		if (weights.length==0)
			continue; // ignore empty lines
//		if (isNaN(parseFloat(weights[weights.length-1])))
//			weights.pop();
		if (weights.length==1 && labels.length==2)
			weights[1] = -weights[0];
		if (weights.length!=labels.length)
			throw new Error("Model does not match SVM-Linear format: there are "+labels.length+" labels ("+labels+") and "+weights.length+" weights ("+weights+")");
		for (var iLabel in labels) {
			var label = labels[iLabel];
			var weight = parseFloat(weights[iLabel]);
			if (Math.abs(weight)>=MIN_WEIGHT)
				mapLabelToMapFeatureToWeight[label][feature]=weight;
		}
	}

	return mapLabelToMapFeatureToWeight;
}



module.exports = SvmLinear;

/**
 * A simple Perceptron implementation.
 *
 * Based on code by John Chesley:  https://github.com/chesles/perceptron
 *
 * the weights vector is a hash (not a numeric array), so the features can be any objects (not just nubmers).
 *
 * @author Erel Segal-haLevi
 * @since 2013-05-27
 * 
 * @param opts optional parameters: <ul>
 *	<li>debug 
 *  <li>default_weight: default weight for a newly discovered feature (default = 0).
 *  <li>do_averaging: boolean, see http://ciml.info/dl/v0_8/ciml-v0_8-ch03.pdf . But count only weight vectors with successful predictions (Carvalho and Cohen, 2006).
 *  <li>do_normalization: boolean, normalize sum of features to 1.
 *	<li>learning_rate: defaults to 0.1.
 *  <li>retrain_count: in batch training mode, how many times to retrain. 0=no retrain. Default=1.
 * 
 */

var hash = require("../../utils/hash");

var PerceptronHash = function(opts) {
	if (!opts) opts = {};

	this.debug = opts.debug||false;
	this.default_weight = opts.default_weight||0;
	this.do_averaging = opts.do_averaging||false;
	this.do_normalization = opts.do_normalization||false;
	this.learning_rate = opts.learning_rate||0.1;
	this.retrain_count = 'retrain_count' in opts? opts.retrain_count: 1;

	this.weights = {};
	if (this.do_averaging) this.weights_sum = {};   // for averaging; see http://ciml.info/dl/v0_8/ciml-v0_8-ch03.pdf . But count only weight vectors with successful predictions (Carvalho and Cohen, 2006).
}


PerceptronHash.prototype = {

	toJSON: function() {
		return {
			weights: this.weights,
			weights_sum: this.weights_sum
		}
	},

	fromJSON: function(json) {
		this.weights = json.weights;
		this.weights_sum = json.weights_sum;
	},

	normalized_features: function (features, remove_unknown_features) {
		features['bias'] = 1;
		if (remove_unknown_features) {
			for (var feature in features)
				if (!(feature in this.weights))
					delete features[feature];
		} 
		if (this.do_normalization) 
			hash.normalize_sum_of_values_to_1(features);
		return features;
	},

	/**
	 * @param features a SINGLE training sample; a hash (feature => value).
	 * @param expected the classification value for that sample (0 or 1)
	 * @return true if the input sample got its correct classification (i.e. no change made).
	 */
	train_features: function(features, expected) {
		for (feature in features) 
			if (!(feature in this.weights)) 
				this.weights[feature] = this.default_weight;

		var result = this.perceive_features(features, /*net=*/false, this.weights); // always use the running 'weights' vector for training, and NOT the weights_sum!

		if (this.debug) console.log('> training ',features,', expecting: ',expected, ' got: ', result);

		if (result != expected) {
			// Current model is incorrect - adjustment needed!
			if (this.debug) console.log('> adjusting weights...', this.weights, features);
			for (var feature in features) 
				this.adjust(result, expected, features[feature], feature);
			if (this.debug) console.log(' -> weights:', this.weights)
		} else {
			if (this.do_averaging) hash.add(this.weights_sum, this.weights);
		}

		return (result == expected);
	},

	/**
	 * Online training (a single sample).
	 *
	 * @param features a SINGLE training sample; a hash (feature => value).
	 * @param expected the classification value for that sample (0 or 1)
	 * @return true if the input sample got its correct classification (i.e. no change made).
	 */
	trainOnline: function(features, expected) {
		return this.train_features(
			this.normalized_features(features, /*remove_unknown_features=*/false), expected);
	},


	/**
	 * Batch training (a set of samples). Uses the option this.retrain_count.
	 *
	 * @param dataset an array of samples of the form {input: {feature1: value1...} , output: 0/1} 
	 */
	trainBatch: function(dataset) {
		var normalized_features = [];
		for (var i=0; i<dataset.length; ++i)
			normalized_features[i] = this.normalized_features(dataset[i].input, /*remove_unknown_features=*/false);

		for (var r=0; r<=this.retrain_count; ++r)
			for (var i=0; i<normalized_features.length; ++i) 
				this.train_features(normalized_features[i], dataset[i].output);
	},


	adjust: function(result, expected, input, feature) {
		var delta = (expected - result) * this.learning_rate * input;
		if (isNaN(delta)) throw new Error('delta is NaN!! result='+result+" expected="+expected+" input="+input+" feature="+feature);
		this.weights[feature] += delta;
		if (isNaN(this.weights[feature])) throw new Error('weights['+feature+'] went to NaN!! delta='+d);
	},


	/**
	 * @param features a SINGLE sample; a hash (feature => value).
	 * @param weights_for_classification the weights vector to use (either the running 'weights' or 'weights_sum').  
	 * @param continuous_output if true, return the net classification value. If false [default], return 0 or 1.
	 * @return the classification of the sample.
	 */
	perceive_features: function(features, continuous_output, weights_for_classification, explain) {
		var score = hash.inner_product(features, weights_for_classification);
		if (this.debug) console.log("> perceive_features ",features," = ",score);
		var result = (continuous_output? score: (score > 0 ? 1 : 0));
		if (explain) {
			result = {
				classification: result,
				explanation: ["Perceptron does not support explanations yet"],
				net_score: score, 
			}
		}
		return result;
	},

	/**
	 * @param features a SINGLE sample, as a hash {feature:value}
	 * @param explain (int) if positive, add an explanation (currently ignored).
	 * @param continuous_output if true, return the net classification score. If false [default], return 0 or 1.
	 * @return the classification of the sample.
	 */
	classify: function(features, explain, continuous_output) {
		return this.perceive_features(
			this.normalized_features(features, /*remove_unknown_features=*/true), 
			continuous_output,
			(this.do_averaging? this.weights_sum: this.weights),
			explain);
	},
}


module.exports = PerceptronHash;

/**
 * Static utilities for hashes (= associative arrays = Javascript objects).
 * 
 * @author Erel Segal-Halevi
 * @since 2013-06
 * @note see performance tests of adding hashes versus arrays here: http://jsperf.com/adding-sparse-feature-vectors
 */


/**
 * create a hash from a string in the format:

 * key1 / value1
 * key2 / value2
 * ...
 *
 * Comments start with '#' and end with end-of-line.
 */
exports.fromString = function(string) {
	var lines = string.split(/[\n\r]/g);
	var hash = {};
	for (var i=0; i<lines.length; ++i) {
		var line = lines[i].trim();
		line = line.replace(/\s*#.*?$/, "");  // remove comments
		if (line.length<1) continue; // skip empty lines

		var parts = line.split(/\s*\/\s*/);
		if (parts.length<2 || !parts[0] || !parts[1]) {
			console.dir(parts);
			throw new Error("empty key or value");
		}
		var key = parts[0];
		var value = parts[1];
		if (key in hash) {
			console.warn("key "+key+" already exists. Old value="+hash[key]+", new value="+value);
		}
		hash[key]=value;
	}
	return hash;
};

/**
 * add one hash to another (target += source)
 * @param target [input and output]
 * @param source [input]: will be added to target.
 */
exports.add  = function(target, source) {
	for (var feature in source) {
		if (!(feature in target))
			target[feature]=0;
		if (toString.call(target[feature]) != '[object Number]') continue;
		target[feature] += source[feature];
	}
	return target;
};

/**
 * add one hash to another (target += scalar * source)
 * @param target [input and output]
 * @param source [input]
 * @param scalar [input]
 */
exports.addtimes  = function(target, scalar, source) {
	for (var feature in source) {
		if (!(feature in target))
			target[feature]=0;
		if (toString.call(target[feature]) != '[object Number]') continue;
		target[feature] += scalar*source[feature];
	}
	return target;
};

/**
 * multiply one hash by another (elementwise multiplication).
 * @param target [input and output]
 * @param source [input]: target will be multiplied by it.
 */
exports.multiply  = function(target, source) {
	for (var feature in source) {
		if (!(feature in target))
			target[feature]=1;
		if (toString.call(target[feature]) != '[object Number]') continue;
		target[feature] *= source[feature];
	}
	return target;
};

/**
 * multiply a hash by a scalar.
 * @param target [input and output]
 * @param source [input]: target will be multiplied by it.
 */
exports.multiply_scalar  = function(target, source) {
	for (var feature in target) {
		if (toString.call(target[feature]) != '[object Number]') continue;
		target[feature] *= source;
	}
	return target;
};

/**
 * calculate the scalar product (dot product) of the given two hashes.
 * @param features [input] - a hash representing a 1-dimensional vector.
 * @param weights [input] - a hash representing a 1-dimensional vector.
 * @return a scalar - the sum of elementwise products.
 * @note Usually, there are much less features than weights.
 */
exports.inner_product = function(features, weights) {
	var result = 0;
	for (var feature in features) {
			if (feature in weights) {
				result += features[feature] * weights[feature];
			} else {
					/* the sample contains a feature that was never seen in training - ignore it for now */ 
			}
	}
	return result;
};

/**
 * calculate the vector dot product of the given two hashes.
 * @param features [input] - a hash representing a 1-dimensional vector.
 * @param weights [input] - a hash of hashes, representing a 2-dimensional matrix.
 * @return a hash - for each key of weights, return the dot product of the given row with the features vector.
 * @note Usually, there are much less features than weights.
 */
exports.inner_product_matrix = function(features, weights) {
	var result = {};
	for (category in weights) {
		result[category] = exports.inner_product(features, weights[category]);
	}
	return result;
};

exports.sum_of_values = function(weights) {
	var result = 0;
	for (var feature in weights)
		result += weights[feature];
	return result;
};

exports.sum_of_absolute_values = function(weights) {
	var result = 0;
	for (var feature in weights)
		result += Math.abs(weights[feature]);
	return result;
};

exports.sum_of_square_values = function(weights) {
	var result = 0;
	for (var feature in weights)
		result += Math.pow(weights[feature],2);
	return result;
};

/**
 * Normalize the given hash, such that the sum of values is 1.
 * Unless, of course, the current sum is 0, in which case, nothing is done. 
 */
exports.normalize_sum_of_values_to_1 = function(features) {
	var sum = exports.sum_of_absolute_values(features);
	if (sum!=0)
		exports.multiply_scalar(features, 1/sum);
};

/**
 * Normalize the given hash, such that the sum of squares of the values is 1.
 * Unless, of course, the current sum is 0, in which case, nothing is done. 
 */
exports.normalize_sum_of_squares_to_1 = function(features) {
	var sum = exports.sum_of_square_values(features);
	if (sum!=0)
		exports.multiply_scalar(features, 1/Math.sqrt(sum));
};


/**
 * @param array [input]
 * @return a string of the given hash, sorted by keys.
 */
exports.stringify_sorted = function(weights, separator) {
	var result = "{" + separator;
	var keys = Object.keys(weights);
	keys.sort();
	var last = keys.length-1;
	for (var i=0; i <= last; i++) {
		var key = keys[i];
		var weight = weights[key]; 
		result += '"'+key+'": '+weight;
		if (i<last) result+=",";
		result += separator;
	}
	result += "}";
	return result;	
};


/**
 * Convert any object to a hash (representing a set):
 *
 * - an array ['a', 'b', 'c'..] to a hash {'a': true, 'b': true, 'c': true};
 * - a string 'a' to a hash {'a': true}.
 */
exports.normalized = function(object) {
	if (Array.isArray(object)) {
		var result = {}; 
		for (var i=0; i<object.length; ++i) 
			result[stringifyIfNeeded(object[i])]=true;
		return result;
	} else if (object instanceof Object) {
		return object;
	} else {
		var result = {};
		result[stringifyIfNeeded(object)]=true; 
		return result;
	}
};


var stringifyIfNeeded = function (label) {
	return (typeof(label)==='string'? label: JSON.stringify(label));
};

