		return (_(aClass).isString()? aClass: JSON.stringify(aClass));
	},

	toJSON : function() {
	},

	fromJSON : function(json) {
	},

	setFeatureLookupTable: function(featureLookupTable) {

	},
}


module.exports = Adaboost;

