console.log("Winnow demo start");
var Winnow = require('./WinnowHash');

var classifier = new Winnow({
	default_positive_weight: 1,
	default_negative_weight: 1,
	threshold: 0,
	do_averaging: false,
	margin: 1,
});

classifier.trainOnline({'a': 1, 'b': 0}, 0);
classifier.trainOnline({'a': 0, 'b': 1}, 0);
